const lines = msg.payload.toString().split("\n");
const data = [];

for (let line of lines) {
    if (!line.startsWith("GPIO")) continue;

    const gpioData = {
        gpio: null,
        level: null,
        mode: null,
        pull: "N/A"

    };

    const parts = line.split(/[:\s]+/);

    for (let i = 0; i < parts.length; i++) {
        if (parts[i] === "GPIO") {
            gpioData.gpio = parseInt(parts[i + 1]);
        } else if (parts[i].startsWith("level=")) {
            gpioData.level = parts[i].split("=")[1];
        } else if (parts[i].startsWith("func=")) {
            gpioData.mode = parts[i].split("=")[1];
        } else if (parts[i].startsWith("pull=")) {
            gpioData.pull = parts[i].split("=")[1];
        }
    }

    if (gpioData.gpio !== null && gpioData.level !== null && gpioData.mode !== null) {
        data.push(gpioData);
    } else {
        node.warn("Nicht erkannt: " + line);
    }
}

msg.payload = data;
return msg;

